package a1_출력;

public class HelloJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 주석
		System.out.println("Hello, Java!");
		// System : 운영체제의 기능을 사용
		// out : 운영체제의 기능중 출력을 담당하는 기능
		// print : 출력을 담당하느 기능중 입력한 만큼만 출력(줄바꿈이 없음)
		// Println : 출력을 담당하는 기능중 한줄을 출력(줄바꿈 자동)
		// ; : 세미콜론, 명령의 마지막 지점을 나타냄 (프린트다음에 줄바꿈을 해도 똑같음)
		System.out.println("Hello, Java2!");
		
		
		//콘솔 실행 단축키 ctrl + F11
		

		
	}

}
